mapper.insert:
INSERT INTO member(
	id, pw, name, birth, gender, email
)VALUES(
	#{id},#{pw},#{name},#{birth},#{gender},#{email}
)

mapper.select:
SELECT id FROM member
WHERE name = #{param1} 
AND birth = #{param2}
AND email = #{param3}

SELECT pw FROM member
WHERE id = #{param1} 
AND birth = #{param2}
AND email = #{param3}
